const ruleTester = new RuleTester();

function formatString(s) {
    const args = Array.prototype.slice.call(arguments, 1);

    return s.replace(/{(\d+)}/g, function (match, number) {
        return typeof args[number] !== "undefined" ? args[number] : match;
    });
}

