             document.getElementById("id").removeEventListener("click", myHandler);
             document.getElementById("id").detachEvent("click", myHandler);
        `,
        // jQuery
        `    // VALID: jQuery
             $("id").on("click", function (obj) { }); 
             $("id").on({ click: myHandler, change: myHandler });
             $("id").off(); 
             $("id").blur(myHandler);
